#!/bin/bash
# Ensure you have a version of NetCat (e.g. NCat) installed

echo ""
echo "Sending a byte"
echo -e '\x80' | nc -nv 127.0.0.1 9001 --send-only
echo ""
echo "Sending two 0xFF bytes"
echo -e '\xFF\xFF' | nc -nv 127.0.0.1 9001 --send-only
echo ""
echo "Sending a NULL byte and a 0xFF byte"
echo -e '\x00\xFF' | nc -nv 127.0.0.1 9001 --send-only
echo ""
echo "Sending two NULL bytes"
echo -e '\x00\x00' | nc -nv 127.0.0.1 9001 --send-only
echo ""
echo "Sending legitimate data"
echo "Hello world!" | nc -nv 127.0.0.1 9001 --send-only
echo ""
echo "Sending much data"
echo "" | nc -nv 127.0.0.1 9001 --send-only